<template>
  <div class=" flex flex-col relative  ">
      <!-- <div class=" absolute puzzle-bg w-56 h-full -ml-56"></div>
      <div class=" absolute right-0 puzzle-bg w-40 h-full -mr-40"></div> -->
      <Puzzle class="animate__animated animate__fadeIn " :key="currentPuzzle" :fn="solvePuzzleAndGoNext" :puzzle='currentPuzzle'/>
        <div class=" flex justify-center flex-wrap space-x-5 mt-8 font-neucha  ">
            <!-- @click="setPuzzle(0)" v-if="!isPuzzlesSolved[0]" :class="{'bg-blue-800':currentNumberOfPuzzle==0, 'bg-gray-800':currentNumberOfPuzzle!=0}" -->
            <div v-for="(item,index) in progressOfPuzzles" class="">
                <div @click="setPuzzle(index);clearNextPuzzleInterval()" v-if="!item" :class="{'bg-blue-800':index==currentNumberOfPuzzle}" class="  hover:bg-blue-800 cursor-pointer w-8 h-8 lg:w-12 lg:h-12  bg-gray-800 rounded-full text-white flex justify-center items-center text-xl lg:text-3xl">{{index+1}}</div>
                <svg @click="setPuzzle(index);clearNextPuzzleInterval()" v-else  class="w-8 h-8 lg:w-12 lg:h-12 cursor-pointer text-green-700 animate__animated animate__jackInTheBox " fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M6.267 3.455a3.066 3.066 0 001.745-.723 3.066 3.066 0 013.976 0 3.066 3.066 0 001.745.723 3.066 3.066 0 012.812 2.812c.051.643.304 1.254.723 1.745a3.066 3.066 0 010 3.976 3.066 3.066 0 00-.723 1.745 3.066 3.066 0 01-2.812 2.812 3.066 3.066 0 00-1.745.723 3.066 3.066 0 01-3.976 0 3.066 3.066 0 00-1.745-.723 3.066 3.066 0 01-2.812-2.812 3.066 3.066 0 00-.723-1.745 3.066 3.066 0 010-3.976 3.066 3.066 0 00.723-1.745 3.066 3.066 0 012.812-2.812zm7.44 5.252a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clip-rule="evenodd"></path></svg>
            </div>                                     
        <div @click="watchNextVideo" class="bg-gray-800 text-white text-lg px-2 py-1 self-center cursor-pointer flex justify-center items-center hover:bg-blue-800">Go next lesson</div>
        </div>

  </div>
</template>

<script>
import Puzzle from '~/components/player/PuzzleForPlayer'
export default {
    props: {
        author:{
            type:Object
        },
        puzzles: {
            type: Array,
            default:[{}]
        },
        solvePuzzle:{
            type:Function
        },
        progressOfPuzzles:{
            type:Array
        },
        watchNextVideo:{
            type:Function
        }
  },
    components:{
        Puzzle
    },
    data() {
        return {
            currentPuzzle:'',
            currentNumberOfPuzzle:'',
            nextPuzzleInterval:null
        }
    },
    created(){
        // console.log('Amazine',this.progressOfPuzzles)
        this.currentNumberOfPuzzle = this.progressOfPuzzles.indexOf(false)
        if(this.currentNumberOfPuzzle==-1) this.currentNumberOfPuzzle=0
        this.currentPuzzle=this.puzzles[this.currentNumberOfPuzzle]
    },
    methods: {
        setPuzzle(index) {
            this.currentNumberOfPuzzle = index
            this.currentPuzzle=this.puzzles[index]
        },
        clearNextPuzzleInterval() {
            clearInterval(this.nextPuzzleInterval)
        },
        solvePuzzleAndGoNext(order) {
            console.log('Var',order)
            console.log('Order',this.currentNumberOfPuzzle)
            if(!this.progressOfPuzzles[this.currentNumberOfPuzzle]) this.solvePuzzle(order)
            for(let i=this.currentNumberOfPuzzle+1;i<this.progressOfPuzzles.length;i++) {
                if(!this.progressOfPuzzles[i]) {
                    this.nextPuzzleInterval = setTimeout(()=>{
                        this.setPuzzle(i)
                    },3000)
                    break;
                }
            }

            // if(this.progressOfPuzzles[this.currentNumberOfPuzzle+1]) 
        }
    }

}
</script>

